import XCTest
@testable import dayTwo

class dayTwoTests: XCTestCase {
    
    func testExample1() {
        let instructionSet = [
            "ULL",
            "RRDDD",
            "LURDL",
            "UUUUD"
        ]
        XCTAssertEqual(Keypad.findCode(instructionSet), 1985)
    }
    
    func testExample2() {
        let instructionSet = [
            "LL"
        ]
        XCTAssertEqual(Keypad.findCode(instructionSet), 4)
    }
    
    func testExample3() {
        let instructionSet = [
            "URRRRRR"
        ]
        XCTAssertEqual(Keypad.findCode(instructionSet), 3)
    }
    
    func testMyInstructions() {
        let instructionSet = [
            "",
            "",
            "",
            "",
            ""
        ]
        XCTAssertEqual(Keypad.findCode(instructionSet), 45973)

    }
   
    func testDiamondUp() {
        let instructionSet = [
            "UUUU"
        ]
        XCTAssertEqual(Keypad.findCodeDiamondPad(instructionSet), "5")
    }
    
    func testDiamondDown() {
        let instructionSet = [
            "DD"
        ]
        XCTAssertEqual(Keypad.findCodeDiamondPad(instructionSet), "5")
    }
    
    func testDiamondExample1() {
        let instructionSet = [
            "ULL",
            "RRDDD",
            "LURDL",
            "UUUUD"
        ]
        XCTAssertEqual(Keypad.findCodeDiamondPad(instructionSet), "5DB3")
    }

    func testMyDiamondInstructions() {
        let instructionSet = [
            "",
            "",
            "",
            "",
            ""
        ]
        XCTAssertEqual(Keypad.findCodeDiamondPad(instructionSet), "27CA4")
        
    }

    static var allTests : [(String, (dayTwoTests) -> () throws -> Void)] {
        return [
            ("testExample1", testExample1),
            ("testExample2", testExample2),
            ("testExample3", testExample3),
            ("testDiamondUp", testDiamondUp),
            ("testDiamondDown", testDiamondDown),
            ("testMyInstructions", testMyInstructions)
        ]
    }
}
